import React, {useState} from "react";

function Form() {

    const[name, setName] = useState('Dinh');

    const inputEvent = (e) => {
        setName(e.tar)
    };
    return(
        <>
            <div className="root__container">
                <h2>Hello {name}</h2>
                <form className="root__form">
                    <input type="text"
                        placeholder="Enter your name"
                        onChange={inputEvent}
                    />
                    <button>Submit</button>
                </form>
            </div>
        </>
    )
}

export default Form;
