    const inputEvent = (e) => {
        setName(e.target.value);
    }

    const inputEventTwo = (e) => {
        setLastname(e.target.value);
    }

    const onSubmits = (e) => {
        e.preventDefault();
        setFirstname(name);
        setLastnameNew(lastName);
