import React, {useState} from "react";



let count = 1;



const Click = () => {
    const IncNum = () => {
        count ++
    }
    return (
        <>
            <h2 className="incnum">{count}</h2>
            <button onClick={IncNum} className="click__me">Click Me</button>
        </>
    )
}

export default Click;
