import React, {useState} from "react";

function ToDo() {

    const [inputList, setInputList] = useState('');
    const [items, setItems] = useState([]);

    const itemEvent = (e) => {
        setInputList(
            e.target.value
        )
    };

    const listOfItems = (e) => {
        e.preventDefault();
        setItems((oldItems) => {
            return [...oldItems, inputList];
        })
    }

    return (
        <>
            <div className="root__container">
                <div className="todo__list">
                    <h2 className="todo_heading">ToDo List</h2>
                    <form className="todo__form">
                        <input type='text'
                            placeholder="Add a item"
                            onChange={itemEvent}
                         />
                        <button onClick={listOfItems}><i className="fa-solid fa-plus"></i></button>
                    </form>
                    <ul className="todo__item">
                        {items.map((itemval) => {
                            return <li>{'<i class="fa-solid fa-xmark"></i>',itemval}</li>
                        }) }                    
                    </ul>
                </div>
            </div>
        </>
    ) 
}

export default ToDo;
