    const [addItem, setAddItem] = useState([]);

    const addNote = (note) => {
        setAddItem((preData) => {
            return [...preData, note]
        })
    }

    return(
        <>
            <Note 
                passNote = {addNote}
            />
            <div className="note__list">
                <NoteItem />             
            </div>
            addItem.map((val, index) => {
                
            })
        </>
    )
}

export default AppNote;
