import React, { useState } from "react";
import RefreshIcon from '@mui/icons-material/Refresh';
import AddIcon from '@mui/icons-material/Add';


function Note(props) {

    const [note, setNote] = useState({
        title: '',
        content: '',
    });

    const inputEvent = (e) => {

        const {name, value} = e.target;

        setNote((preData) => {
            return{
                ...preData,
                [name]: value,
            }
        })
    }

    const addEvent = () => {
        props.passNote(note)
    }
    return(
        <>
            <div className="note__div">
                <input type="text"
                    placeholder="Title"
                    name="title"
                    autoComplete="off"
                    value={note.title}
                    onChange={inputEvent}>                    
                </input> <br />
                <textarea rows="" column=""
                        placeholder="Write a note..."
                        name="content"
                        value={note.content}
                        onChange={inputEvent}>                 
                </textarea>
                {/* <div className="note__refresh">
                        <RefreshIcon />
                </div>               */}
                <Button onClick={addEvent}>
                    <AddIcon />
                </Button>
            </div>
            {/* <div className="note__list">
                <div className="note__item">
                    <p>1st</p>
                    <p>hoang</p>
                    <Button><DeleteOutlinedIcon /></Button>
                </div>
            </div> */}
        </>
    )
}

export default Note;
